package geneticcodes;/** * @author Wei Yuzhang * this abstract class is to define four methods of translating RNA to protein! * you should implement this interface to realize translate for Genetic Codes * */public abstract class AbstractGeneticCodes {	protected String name;		 /**     *      * @param seq     * @return null     *      */	public String[] translate(String seq){		return null;	}		 /**     *      * @param seq orf     * @return null     *      */	public String[] translate(String seq, OpenReadingFrame orf){		return null;	}		 /**     *      * @param seq singleCh_aa     * @return null     *      */	public String[] translate(String seq, boolean singleCh_aa){		return null;	}		 /**     *      * @param seq orf singleCh_aa     * @return null     *      */	public String[] translate(String seq, OpenReadingFrame orf, boolean singleCh_aa){		return null;	}}