package geneticcodes;/** * @author Wei Yuzhang * this interface is to predefine the relations between different meanings for each Amino Acid * you can get the relations easily by call the array name *  */public interface AminoAcid {	public final String[] Alanine = {"A", "ALA+", "Alanine"};	public final String[] Arginie = {"R", "ARG", "Arginie"};	public final String[] Asparagine = {"N", "ASN", "Asparagine"};	public final String[] AspaticAcid = {"D", "ASP", "AspaticAcid"};	public final String[] Cysteine = {"C", "CYS", "Cysteine"};	public final String[] GlutamicAcid = {"E", "GLU", "GlutamicAcid"};	public final String[] Glutamine = {"Q", "GLN", "Glutamine"};	public final String[] Glycine = {"G", "GLY", "Glycine"};	public final String[] Histidine = {"H", "HIS", "Histidine"};	public final String[] Isoleucine = {"I", "ILE", "Isoleucine"};	public final String[] Leucine = {"L", "LEU", "Leucine"};	public final String[] Lysine = {"K", "LYS", "Lysine"};	public final String[] Methionine = {"M", "MET", "Methionine"};	public final String[] Phenylalanine = {"F", "PHE", "Phenylalanine"};	public final String[] Proline = {"P", "PRO", "Proline"};	public final String[] Serine = {"S", "SER", "Serine"};	public final String[] Threonine = {"T", "THR", "Threonine"};	public final String[] Tryptophan = {"W", "TRP", "Tryptophan"};	public final String[] Tyrosine = {"Y", "TYR", "Tyrosine"};	public final String[] Valine = {"V", "VAL", "Valine"};	public final String[] Missing = {".",".","unknown"};	public final String[] Null = {"-","-","unknown"};	public final String[] STOP = {"*","*","STOP"};}